INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/671', 'Harry Potter and the Philosopher''s Stone', 'Harry Potter has lived under the stairs at his aunt and uncle''s house his whole life. But on his 11th birthday, he learns he''s a powerful wizard -- with a place waiting for him at the Hogwarts School of Witchcraft and Wizardry. As he learns to harness his newfound powers with the help of the school''s kindly headmaster, Harry uncovers the truth about his parents'' deaths -- and about the villain who''s to blame.', 'Let the Magic Begin.', 152, 125000000, 976475550, 'https://image.tmdb.org/t/p/original//hziiv14OpD73u9gAak4XDDfBKa2.jpg', 'https://image.tmdb.org/t/p/w342//wuMc08IPKEatf9rnMNXvIDxqP4W.jpg', 'https://www.imdb.com/title/tt0241527', 'en', '2001-11-16')
