INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/278', 'The Shawshank Redemption', 'Framed in the 1940s for the double murder of his wife and her lover, upstanding banker Andy Dufresne begins a new life at the Shawshank prison, where he puts his accounting skills to work for an amoral warden. During his long stretch in prison, Dufresne comes to be admired by the other inmates -- including an older prisoner named Red -- for his integrity and unquenchable sense of hope.', 'Fear can hold you prisoner. Hope can set you free.', 142, 25000000, 28341469, 'https://image.tmdb.org/t/p/original//tXHpvlr5F7gV5DwgS7M5HBrUi2C.jpg', 'https://image.tmdb.org/t/p/w342//q6y0Go1tsGEsmtFryDOJo3dEmqu.jpg', 'https://www.imdb.com/title/tt0111161', 'en', '1994-09-23')
