INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/12', 'Finding Nemo', 'Nemo, an adventurous young clownfish, is unexpectedly taken from his Great Barrier Reef home to a dentist''s office aquarium. It''s up to his worrisome father Marlin and a friendly but forgetful fish Dory to bring Nemo home -- meeting vegetarian sharks, surfer dude turtles, hypnotic jellyfish, hungry seagulls, and more along the way.', 'There are 3.7 trillion fish in the ocean. They''re looking for one.', 100, 94000000, 940335536, 'https://image.tmdb.org/t/p/original//dFYguAfeVt19qAbzJ5mArn7DEJw.jpg', 'https://image.tmdb.org/t/p/w342//eHuGQ10FUzK1mdOY69wF5pGgEf5.jpg', 'https://www.imdb.com/title/tt0266543', 'en', '2003-05-30')
INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/105', 'Back to the Future', 'Eighties teenager Marty McFly is accidentally sent back in time to 1955, inadvertently disrupting his parents'' first meeting and attracting his mother''s romantic interest. Marty must repair the damage to history by rekindling his parents'' romance and - with the help of his eccentric inventor friend Doc Brown - return to 1985.', 'He''s the only kid ever to get into trouble before he was born.', 116, 19000000, 381109762, 'https://image.tmdb.org/t/p/original//fq3wyOs1RHyz2yfzsb4sck7aWRG.jpg', 'https://image.tmdb.org/t/p/w342//xlBivetfrtF84Yx0zISShnNtHYe.jpg', 'https://www.imdb.com/title/tt0088763', 'en', '1985-07-03')
