INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/675', 'Harry Potter and the Order of the Phoenix', 'Returning for his fifth year of study at Hogwarts, Harry is stunned to find that his warnings about the return of Lord Voldemort have been ignored. Left with no choice, Harry takes matters into his own hands, training a small group of students – dubbed ''Dumbledore''s Army'' – to defend themselves against the dark arts.', 'Evil Must Be Confronted.', 138, 150000000, 938212738, 'https://image.tmdb.org/t/p/original//pkxPkHOPJjOvzfQOclANEBT8OfK.jpg', 'https://image.tmdb.org/t/p/w342//s836PRwHkLjrOJrfW0eo7B4NJOf.jpg', 'https://www.imdb.com/title/tt0373889', 'en', '2007-06-28')
