INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/281957', 'The Revenant', 'In the 1820s, a frontiersman, Hugh Glass, sets out on a path of vengeance against those who left him for dead after a bear mauling.', '(n. One who has returned, as if from the dead.)', 157, 135000000, 532950503, 'https://image.tmdb.org/t/p/original//nMfH0LMMFk2ovyURkYvyTI99nSo.jpg', 'https://image.tmdb.org/t/p/w342//ji3ecJphATlVgWNY0B0RVXZizdf.jpg', 'https://www.imdb.com/title/tt1663202', 'en', '2015-12-25')
INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/12444', 'Harry Potter and the Deathly Hallows: Part 1', 'Harry, Ron and Hermione walk away from their last year at Hogwarts to find and destroy the remaining Horcruxes, putting an end to Voldemort''s bid for immortality. But with Harry''s beloved Dumbledore dead and Voldemort''s unscrupulous Death Eaters on the loose, the world is more dangerous than ever.', 'One Way… One Fate… One Hero.', 146, 250000000, 954305868, 'https://image.tmdb.org/t/p/original//AqLcLsGGTzAjm3pCCq0CZCQrp6m.jpg', 'https://image.tmdb.org/t/p/w342//iGoXIpQb7Pot00EEdwpwPajheZ5.jpg', 'https://www.imdb.com/title/tt0926084', 'en', '2010-10-17')
INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/767', 'Harry Potter and the Half-Blood Prince', 'As Harry begins his sixth year at Hogwarts, he discovers an old book marked as ''Property of the Half-Blood Prince'', and begins to learn more about Lord Voldemort''s dark past.', 'Dark Secrets Revealed', 153, 250000000, 933959197, 'https://image.tmdb.org/t/p/original//kT8bDEAgEYBKhRJtqM97qTw6uRW.jpg', 'https://image.tmdb.org/t/p/w342//z7uo9zmQdQwU5ZJHFpv2Upl30i1.jpg', 'https://www.imdb.com/title/tt0417741', 'en', '2009-07-07')
INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/100402', 'Captain America: The Winter Soldier', 'After the cataclysmic events in New York with The Avengers, Steve Rogers, aka Captain America is living quietly in Washington, D.C. and trying to adjust to the modern world. But when a S.H.I.E.L.D. colleague comes under attack, Steve becomes embroiled in a web of intrigue that threatens to put the world at risk. Joining forces with the Black Widow, Captain America struggles to expose the ever-widening conspiracy while fighting off professional assassins sent to silence him at every turn. When the full scope of the villainous plot is revealed, Captain America and the Black Widow enlist the help of a new ally, the Falcon. However, they soon find themselves up against an unexpected and formidable enemy—the Winter Soldier.', 'In heroes we trust.', 136, 170000000, 714766572, 'https://image.tmdb.org/t/p/original//yHB0eNR8rvCpn0VdghEwBsXAC0N.jpg', 'https://image.tmdb.org/t/p/w342//5r1zWuzbsVAYSPzvCecAReGhN7k.jpg', 'https://www.imdb.com/title/tt1843866', 'en', '2014-03-20')
INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/10681', 'WALL·E', 'WALL·E is the last robot left on an Earth that has been overrun with garbage and all humans have fled to outer space. For 700 years he has continued to try and clean up the mess, but has developed some rather interesting human-like qualities. When a ship arrives with a sleek new type of robot, WALL·E thinks he''s finally found a friend and stows away on the ship when it leaves.', 'After 700 years of doing what he was built for, he''ll discover what he was meant for.', 98, 180000000, 521311860, 'https://image.tmdb.org/t/p/original//ai2FicMUxLCurVkjtYdSvVDWRmS.jpg', 'https://image.tmdb.org/t/p/w342//hbhFnRzzg6ZDmm8YAmxBnQpQIPh.jpg', 'https://www.imdb.com/title/tt0910970', 'en', '2008-06-22')
