INSERT INTO [Movies] ([TmdbUrl], [Title], [OverView], [Tagline], [Runtime], [Budget], [Revenue], [BackdropUrl], [PosterUrl], [ImdbUrl], [OriginalLanguage], [ReleaseDate]) VALUES ('https://www.themoviedb.org/movie/374720', 'Dunkirk', 'The story of the miraculous evacuation of Allied soldiers from Belgium, Britain, Canada and France, who were cut off and surrounded by the German army from the beaches and harbour of Dunkirk between May 26th and June 4th 1940 during World War II.', 'Survival is Victory', 107, 100000000, 525573161, 'https://image.tmdb.org/t/p/original//ut4ALkOwEl01nqODS3s6q4C2uWp.jpg', 'https://image.tmdb.org/t/p/w342//ebSnODDg9lbsMIaWg2uAbjn7TO5.jpg', 'https://www.imdb.com/title/tt5013056', 'en', '2017-07-19')
